Java<pre><code>import java.util.HashSet;
import java.util.Set;

public class SetOperationsExample {
    public static void main(String[] args) {
        // 创建两个集合
        Set&lt;String&gt; set1 = new HashSet&lt;&gt;();
        set1.add("A");
        set1.add("B");
        set1.add("C");

        Set&lt;String&gt; set2 = new HashSet&lt;&gt;();
        set2.add("B");
        set2.add("C");
        set2.add("D");

        // 交集（保留两个集合共有的元素）
        Set&lt;String&gt; intersection = new HashSet&lt;&gt;(set1);
        intersection.retainAll(set2);
        System.out.println("Intersection (交集): " + intersection);  // 输出：[B, C]

        // 并集（两个集合所有元素的组合）
        Set&lt;String&gt; union = new HashSet&lt;&gt;(set1);
        union.addAll(set2);
        System.out.println("Union (并集): " + union);  // 输出：[A, B, C, D]

        // 差集（只存在于 set1 中的元素）
        Set&lt;String&gt; difference = new HashSet&lt;&gt;(set1);
        difference.removeAll(set2);
        System.out.println("Difference (差集): " + difference);  // 输出：[A]
    }
}
</code></pre>

Java<pre><code>import java.util.HashSet;
import java.util.Set;

public class ArrayIntersection {
    public int[] intersection(int[] nums1, int[] nums2) {
        // 将第一个数组的元素放入 Set 中
        Set&lt;Integer&gt; set1 = new HashSet&lt;&gt;();
        for (int num : nums1) {
            set1.add(num);
        }

        // 将第二个数组的元素放入 Set 中
        Set&lt;Integer&gt; set2 = new HashSet&lt;&gt;();
        for (int num : nums2) {
            set2.add(num);
        }

        // 直接根据大小判断，遍历较小的集合
        if (set1.size() &gt; set2.size()) {
            return getIntersection(set2, set1);
        } else {
            return getIntersection(set1, set2);
        }
    }

    public int[] getIntersection(Set&lt;Integer&gt; smallerSet, Set&lt;Integer&gt; largerSet) {
        // 创建一个 Set 用于存储交集
        Set&lt;Integer&gt; intersectionSet = new HashSet&lt;&gt;();
        for (int num : smallerSet) {
            if (largerSet.contains(num)) {
                intersectionSet.add(num);
            }
        }

        // 将交集集合转换为数组并返回
        int[] intersection = new int[intersectionSet.size()];
        int index = 0;
        for (int num : intersectionSet) {
            intersection[index++] = num;
        }
        return intersection;
    }
}
</code></pre>

Java<pre><code>import java.util.HashSet;
import java.util.Set;

public class ArrayIntersection {
    public int[] intersection(int[] nums1, int[] nums2) {
        // 将两个数组转换为 Set
        Set&lt;Integer&gt; set1 = new HashSet&lt;&gt;();
        for (int num : nums1) {
            set1.add(num);
        }

        Set&lt;Integer&gt; set2 = new HashSet&lt;&gt;();
        for (int num : nums2) {
            set2.add(num);
        }

        // 使用 retainAll() 保留交集
        set1.retainAll(set2);

        // 将交集转换为数组并返回
        int[] result = new int[set1.size()];
        int index = 0;
        for (int num : set1) {
            result[index++] = num;
        }
        return result;
    }
}
