    int maxArea(vector&lt;int&gt;&amp; height) {
        int left = 0;
        int right = height.size() - 1;
        int maxArea = 0;

        while (left &lt; right) {
            // 计算当前容器的面积
            int area = min(height[left], height[right]) * (right - left);
