    vector&lt;int&gt; findClosestElements(vector&lt;int&gt;&amp; arr, int k, int x) {
        int left = 0, right = arr.size() - 1;

        // 当区间长度大于 k 时，不断移动指针
        while (right - left &gt;= k) {
            // 比较两端的距离，移动较远的一端
            if (abs(arr[left] - x) &gt; abs(arr[right] - x)) {
                left++;
            } else {
                right--;
            }
