                    window_count -= customers[left];  // 移除窗口左端的顾客
                }
                left++;
            }

            ans = max(ans, window_count);  // 记录最大窗口内的顾客数量
            right++;
        }

        // 计算老板不生气时的顾客人数
        for (int i = 0; i &lt; n; ++i) {
            if (grumpy[i] == 0) {
                ans += customers[i];  // 老板不生气时，这些顾客一直是满意的
            }
        }

        return ans;
    }
};
