C++<pre><code>class Solution {
public:
    vector&lt;int&gt; maxSlidingWindow(vector&lt;int&gt;&amp; nums, int k) {
        int n = nums.size();
        vector&lt;int&gt; window; // 用来存储当前窗口中的元素索引
        vector&lt;int&gt; result; // 用于存储结果的数组

        for (int i = 0; i &lt; n; i++) {
