            if (!window.empty() &amp;&amp; window[0] &lt;= i - k) {
                window.erase(window.begin());
            }

            // 移除窗口中所有小于当前元素的元素
            while (!window.empty() &amp;&amp; nums[window.back()] &lt;= nums[i]) {
                window.pop_back();
            }

            // 将当前元素索引添加到窗口
            window.push_back(i);

            // 当窗口的大小达到k时，记录窗口的最大值
            if (i &gt;= k - 1) {
                result.push_back(nums[window[0]]);  // 窗口的第一个元素是最大值的索引
            }
        }

        return result;
    }
};
