        for (int num : nums) {
            total += num;
        }

        // 遍历数组，计算每个下标的左侧和，并检查是否满足条件
        int leftSum = 0;
        for (int i = 0; i &lt; nums.size(); i++) {
            // 如果左侧和等于右侧和（右侧和 = 总和 - 左侧和 - 当前元素）
            if (leftSum == total - leftSum - nums[i]) {
                return i;
            }
            // 更新左侧和
            leftSum += nums[i];
        }

        // 如果没有找到中心下标，返回 -1
        return -1;
    }
};
