Java<pre><code>class Solution {
    public int busyStudent(int[] startTime, int[] endTime, int queryTime) {
        // 找到结束时间的最大值
        int maxTime = 0;
        for (int end : endTime) {
            maxTime = Math.max(maxTime, end);
        }

        // 初始化差分数组，长度为 maxTime + 2，避免越界
        int[] diff = new int[maxTime + 2];

        // 更新差分数组
        for (int i = 0; i &lt; startTime.length; i++) {
            diff[startTime[i]] += 1;          // 开始写作业
            diff[endTime[i] + 1] -= 1;        // 停止写作业
        }

        // 计算前缀和，得到每个时间点的在做作业的学生人数
        int currentCount = 0;
        for (int i = 0; i &lt;= queryTime; i++) {
            currentCount += diff[i];
        }

        return currentCount;  // 返回在 queryTime 时正在做作业的学生人数
    }
}
