            if (i + 2 &lt; n &amp;&amp; s[i + 2] == '#') {  // 检查是否是 '10#' 到 '26#'
                int num = (s[i] - '0') * 10 + (s[i + 1] - '0');  // 计算两位数
                result += mapping[num - 1];  // 将其映射为字母
                i += 2;  // 跳过数字和 '#'
            } else {  // 单个数字 '1' 到 '9'
                int num = s[i] - '0';  // 转换成数字
                result += mapping[num - 1];  // 映射为字母
            }
        }
        return result;
    }
};
