    // 检查一个数字是否是自除数
    public boolean isSelfDividing(int num) {
        int originalNum = num;

        while (num &gt; 0) {
            int digit = num % 10;  // 提取数字的最后一位
            if (digit == 0 || originalNum % digit != 0) {
                return false;  // 任何一位为0或者不能整除原数字，则不是自除数
