        ArrayList&lt;Integer&gt; result = new ArrayList&lt;&gt;();

        // 遍历范围 [left, right]，逐个检查是否为自除数
        for (int i = left; i &lt;= right; i++) {
            if (isSelfDividing(i)) {
                result.add(i);  // 如果是自除数，加入结果列表
            }
        }

        return result;
