Java<pre><code>import java.util.ArrayList;

public class Solution {
    public ArrayList&lt;Integer&gt; addToArrayForm(int[] num, int k) {
        ArrayList&lt;Integer&gt; result = new ArrayList&lt;&gt;();
        int carry = 0; // 初始化进位
        int n = num.length; // 数组长度

        // 从数组的最后一位开始处理
        for (int i = n - 1; i &gt;= 0; i--) {
            int sum = num[i] + (k % 10) + carry; // 当前位的和
            result.add(sum % 10); // 当前位结果
            carry = sum / 10; // 更新进位
            k /= 10; // 移动到 k 的下一位
        }

        // 处理 k 可能还有的剩余位
        while (k &gt; 0 || carry &gt; 0) {
            int sum = (k % 10) + carry; // 当前位的和
            result.add(sum % 10); // 当前位结果
            carry = sum / 10; // 更新进位
            k /= 10; // 移动到 k 的下一位
        }

        // 由于我们是从后向前构建的结果，需要反转
        ArrayList&lt;Integer&gt; finalResult = new ArrayList&lt;&gt;();
        for (int j = result.size() - 1; j &gt;= 0; j--) {
            finalResult.add(result.get(j));
        }

        return finalResult;
    }
}
