            while (left &lt; right &amp;&amp; !Character.isLetterOrDigit(s.charAt(left))) {
                left++;
            }
            // 找到右侧的字母数字字符
            while (left &lt; right &amp;&amp; !Character.isLetterOrDigit(s.charAt(right))) {
                right--;
            }
            // 比较字符，忽略大小写
            if (Character.toLowerCase(s.charAt(left)) != Character.toLowerCase(s.charAt(right))) {
                return false;
            }
            left++;
            right--;
        }
        return true;     
    }
}
</code></pre>

